$(".vip_box ul li").on("click",function(){
    $(this).children("p").addClass("cur-dredge");
    $(this).siblings().children("p").removeClass("cur-dredge");
    var month,price;
    month=$(this).find("#coin_month").text();
    price=$(this).find("#coin_price").text();
    $('input[type="hidden"]').attr('data-month',month);
    $('input[type="hidden"]').attr('data-price',price);
})


$(".release .choose a").on("click",function(){
    $(this).addClass("active");
    $(this).siblings().removeClass("active");
});
$(".release .region a").on("click",function(){
    $(this).addClass("active");
    $(this).siblings().removeClass("active");
});
$(".release .choose .radio").on("click",function(){
    $(this).children("label").addClass("on");
    $(this).siblings().children("label").removeClass("on");
    $(this).children("label").find("span").removeClass("off");
    $(this).siblings().children("label").find("span").addClass("off");
    $(this).children("label").find("span").addClass("on");
    $(this).siblings().children("label").find("span").removeClass("on");
});



$(function(){
    banner('.swiper-container');
})
 /**
  * 轮播图
  * @type {Swiper}
  */
function banner(className) {
     var topSlider=new Swiper(className, {
            slidesPerView: 1,
            centeredSlides: true,
            autoplay: 3000,
            loop: true,
            scrollbar:'.swiper-scrollbar',
            autoplayDisableOnInteraction: true
        });
}
