/**
 *订单
 */
$(function(){
        var cur_li=$('#tab li');
        cur_li.on('touchstart',function(){
        var _this=$(this).index();
        $(this).children().addClass('cur-tab').parent().siblings().children().removeClass('cur-tab');
        $('.page').slideUp('2000').eq(_this).slideDown('2000');
        //加载数据
        if(_this==0){
            console.log(0)
        }else if(_this==1){
            console.log(1)
        }else{
            console.log(2)
        }
    });
});

$("#radioall").on("click",function(){
    if($(this).is(':checked')){
        $('input[type="checkbox"]').prop("checked",true);
    }else{
        $('input[type="checkbox"]').prop("checked",false);
    }
})

$(function(){var i=$("#tab li");i.on("touchstart",function(){var i=$(this).index();$(this).children().addClass("cur-tab").parent().siblings().children().removeClass("cur-tab"),$(".page").slideUp("2000").eq(i).slideDown("2000"),console.log(0==i?0:1==i?1:2)})});

